(function(a){a.fn.textScroll=function(c,b,d){a(this).wrap('<marquee id="scrollWrap" loop="infinite" direction="up"></marquee>');if(c){if(c.toString().indexOf("px")>=0){a("#scrollWrap").attr("width",c)}else{c=c+"px";a("#scrollWrap").attr("width",c)}if(c=="100%"){a("#scrollWrap").attr("width","100%")}}else{a("#scrollWrap").attr("width","100%")}if(b){if(b.toString().indexOf("px")>=0){a("#scrollWrap").attr("height",b)}else{b=b+"px";a("#scrollWrap").attr("height",b)}if(b=="100%"){a("#scrollWrap").attr("height","100%")}}else{a("#scrollWrap").attr("height","100px")}if(d){a("#scrollWrap").attr("scrolldelay",d)}else{a("#scrollWrap").attr("scrolldelay",200)}}})(jQuery);